/**
 * All of the code for widget admin-facing JavaScript dropdown channels list functionalty
 */


    // Get selected channel
    function SelectFunction() {
        jQuery(".team_feed_channel").click( function() {
            var channelId = jQuery( this ).children(":selected").attr("value");
            var channelName = jQuery( this ).children(":selected").attr("name");

            jQuery(".channelId").val( channelId );
            jQuery(".channelName").val( channelName );
        });
    }
