/**
 * All of the code for widget public-facing JavaScript CustomScrollbar functionalty
 */

jQuery(document).ready( function() {

    // Responsive scroll
    jQuery(window).load( function() {

        jQuery("ul#team_feed_channel").mCustomScrollbar({
            // Set both axis scrollbars
            axis:"yx",
            // Auto-expand content to accommodate floated elements
            advanced:{ autoExpandHorizontalScroll:true },
            // Change mouse-wheel axis on-the-fly
            callbacks:{
                onOverflowY:function(){
                    var opt = jQuery( this ).data( "mCS" ).opt;
                    if ( opt.mouseWheel.axis!=="y" ) opt.mouseWheel.axis = "y" ;
                }
            }
        });
    });

});